import { StrictMode } from "react";
import { createRoot } from "react-dom/client";
import "./styles.css";

function App() {
  return (
    <div className="card">
      <Avatar />
      <div className="data">
        <Intro />
        <SkillList />
      </div>
    </div>
  );
}

const skills = [
  {
    skill: "Surfing",
    level: "Advanced",
    color: "#2662EA",
  },
  {
    skill: "Dancing",
    level: "Beginner",
    color: "#EFD81D",
  },
  {
    skill: "Writing",
    level: "Beginner",
    color: "#C3DCAF",
  },
  {
    skill: "Cooking",
    level: "Intermediate",
    color: "#60DAFB",
  },
  {
    skill: "Gaming",
    level: "Intermediate",
    color: "#19AAFC",
  },
];

function Avatar() {
  return <img className="avatar" src="avatar.jpeg" alt="Cody Iddings" />;
}

function Intro() {
  return (
    <div>
      <h1>Cody Iddings</h1>
      <p>
        I'm a co-founder of Makeshapes. We're on a mission to democratise
        high-quality team learning. Our unique approach allows organisations to
        scale learning and culture change in a way not previously possible.
      </p>
    </div>
  );
}

function SkillList(skills) {
  return (
    <div className="skill-list">
      <Skill name="{skills.skill}" />
