import { StrictMode } from "react";
import { createRoot } from "react-dom/client";
import "./styles.css";

function App() {
  return (
    <div className="card">
      <Avatar />
      <div className="data">
        <Intro />
        <SkillList />
      </div>
    </div>
  );
}

const skills = [
  {
    skill: "Surfing",
    level: "Advanced",
    color: "#2662EA",
  },
  {
    skill: "Dancing",
    level: "Beginner",
    color: "#EFD81D",
  },
  {
    skill: "Writing",
    level: "Beginner",
    color: "#C3DCAF",
  },
  {
    skill: "Cooking",
    level: "Intermediate",
    color: "#60DAFB",
  },
  {
    skill: "Gaming",
    level: "Intermediate",
    color: "#19AAFC",
  },
];

function Avatar() {
  return <img className="avatar" src="avatar.jpeg" alt="Cody Iddings" />;
}

function Intro() {
  return (
    <div>
      <h1>Cody Iddings</h1>
      <p>
        I'm a co-founder of Makeshapes. We're on a mission to democratise
        high-quality team learning. Our unique approach allows organisations to
        scale learning and culture change in a way not previously possible.
      </p>
    </div>
  );
}

function SkillList() {
  const skillTree = skills;
  return (
    <div className="skill-list">
      {skills.map((skill) => (
        <Skill
          skillObj={skill}
          // skill={skill.skill}
          // color={skill.color}
          // level={skill.level}
          key={skill.skill}
        />
      ))}
    </div>
  );
}

function Skill({ skillObj }) {
  console.log(skillObj);
  const bgColor = { backgroundColor: skillObj.color };
  // const levelEmojis = {
  //   Beginner: "ðŸŒ±", // Emoji for Beginner
  //   Intermediate: "ðŸ”¥", // Emoji for Intermediate
  //   Advanced: "ðŸ’ª", // Emoji for Advanced
  // };
  // const textColor =
  //   parseInt(bgColor.replace("#", ""), 16) > 0xffffff / 2 ? "#000" : "#fff";
  return (
    <div className="skill" style={bgColor}>
      <p>{skillObj.skill}</p>
      <span>
        {skillObj.level === "Beginner"
          ? "ðŸŒ±"
          : skillObj.level === "Intermediate"
          ? "ðŸ”¥"
          : skillObj.level === "Advanced"
          ? "ðŸ’ª"
          : ""}
      </span>
    </div>
  );
}

const rootElement = document.getElementById("root");
const root = createRoot(rootElement);

root.render(
  <StrictMode>
    <App />
  </StrictMode>
);
